<script>
import { gsap, Power3, TimelineMax } from "gsap";
import vCaseLoader from "@/components/v-case-loader";
import { mapGetters } from "vuex";
export default {
  name: "App",
  components: {
    vCaseLoader,
  },
  data() {
    return {
      menu_show: false,
    };
  },
  methods: {
    menuShow() {
      gsap
        .to(".menu", 0, {
          opacity: 1,
          zIndex: 2,
        })
        .then(() => {
          gsap
            .fromTo(
              ".menu__link",
              {
                transform: "translateX(-100%)",
              },
              {
                transform: "translateX(0%)",
                stagger: 0.1,
                ease: Power3.easeInOut,
                duration: 1,
              }
            )
            .then(() => {
              gsap.to(".menu__close", 0.4, {
                opacity: 1,
                zIndex: 3,
                transform: "translate(-50%, 10px)",
              });
            });
        });
    },
    menuClose() {
      gsap
        .to(".menu__close", 0.4, {
          opacity: 0,
          zIndex: 0,
          transform: "translate(-50%, 0px)",
        })
        .then(() => {
          gsap
            .to(".menu__link", 1, {
              transform: "translateX(100%)",
              stagger: 0.1,
              ease: Power3.easeInOut,
            })
            .then(() => {
              gsap.set(".menu", {
                opacity: 0,
                zIndex: -1,
              });
            });
        });
    },
    aboutPage(action, ref) {
      let tl = new TimelineMax({});
      let title = ref.childNodes[0].childNodes[0];
      let subtitle = ref.childNodes[0].childNodes[1];

      if (action == "enter") {
        tl.to(title, 0.8, {
          transform: `translateY(-15px)`,
          ease: Power3.easeInOut,
        });
        tl.to(
          subtitle,
          0.8,
          {
            height: "auto",
            ease: Power3.easeInOut,
          },
          "-=0.7"
        );
      } else if (action == "leave") {
        tl.to(title, 0.8, {
          transform: `translateY(0px)`,
          ease: Power3.easeInOut,
        });
        tl.to(
          subtitle,
          0.8,
          {
            height: "0%",
            ease: Power3.easeInOut,
          },
          "-=0.7"
        );
      }
    },
  },
  computed: {
    ...mapGetters(["CASE_DATA"]),
  },
  mounted() {},
};
</script>

